var rowCount = this.data().length-1,		insertedRow = this.row(rowCount).data(),		tempRow;	for (var i=rowCount;i>=index;i--) {		tempRow = this.row(i-1).data();		this.row(i).data(tempRow);		this.row(i-1).data(insertedRow);	}     	//refresh the current page	this.page(currentPage).draw(false);});	var provider_inbox_message;$(document).ready(function(){		provider_inbox_message = $('#provider_inbox_data').DataTable( {		"aaSorting": [],    	"pagingType": "full_numbers"	});	provider_inbox_ajax_message('<?php echo base_url(); ?>','<?php echo $organization['organization_id'] ?>','<?php echo $this->security->get_csrf_hash(); ?>');});function mysql_time_to_javascript_time(mysqltime){	var datetimesplit = mysqltime.split(" ");	var datesplit = datetimesplit[0].split('-');	var timesplit = datetimesplit[1].split(':');	var hour;	var meridian;	if(parseInt(timesplit[0]) > 12)	{		hour = parseInt(timesplit[0])-12;		meridian = 'pm';			}	else	{		hour = parseInt(timesplit[0]);		meridian = 'pm';	}	return datesplit[2]+'/'+datesplit[1]+'/'+datesplit[0]+' '+hour+':'+timesplit[1]+' '+meridian}function provider_inbox_ajax_message(url,id,csrf){		var lastmessageid = $('.provider_inbox_last_id').length > 0 ? parseInt($(document).find('.provider_inbox_last_id').val()):0;	    var message = $.ajax({	        type: "POST",	        url: url+"provider/inbox/message",	        data : { orgid : id , csrf_token : csrf,lastid: lastmessageid},	        dataType: 'json',	        async: false	    }).complete(function(){	        setTimeout(function(){provider_inbox_ajax_message(url,id,csrf);}, 60000);	    }).responseText;	    var json_data = $.parseJSON(message);	    var retrivedatacount = json_data.length;	    if(retrivedatacount > 0){	    	$.each(json_data, function(i){	    		var inbox_id_increase = parseInt(lastmessageid)+1;	    		var converted_time = mysql_time_to_javascript_time(json_data[i].inbox_created_date);	    		provider_inbox_message.row.addByPos(['<input type="checkbox" class="provider_inbox_id" data-msg-id ="'+inbox_id_increase+'">',json_data[i].candidate_name,'<a class="btn" data-toggle="modal" data-target="#applicant_msg_act" data-backdrop="static" data-keyboard="false">Hi, I wish to apply for '+json_data[i].vacancies_job_title+'</a>',converted_time],1);	    	});      		provider_inbox_message.draw();      		$(document).find('.provider_inbox_last_id').val(lastmessageid+retrivedatacount);	    }	}</script>